require 'rails_helper'

RSpec.describe BasicWorkExperience, type: :model do
  before do
    @basic_work_experience = FactoryBot.build(:basic_work_experience)
  end

  it "has a valid factory" do
    expect(@basic_work_experience).to be_valid
  end

  context "validation" do
    it "allows 150 character affiliation" do
      @basic_work_experience.affiliation = "BCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTU"
      expect(@basic_work_experience).to be_valid
    end

    it "does not allow over 150 character affiliation" do
      @basic_work_experience.affiliation = "ABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTU"
      @basic_work_experience.valid?
      expect(@basic_work_experience.errors[:affiliation]).to include("は150文字以内で入力してください")
    end

    it "allows 50 character nearest_station" do
      @basic_work_experience.nearest_station = "BCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXY"
      expect(@basic_work_experience).to be_valid
    end

    it "does not allow over 50 character nearest_station" do
      @basic_work_experience.nearest_station = "ABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXY"
      @basic_work_experience.valid?
      expect(@basic_work_experience.errors[:nearest_station]).to include("は50文字以内で入力してください")
    end

    it "allows 20 character carrer_period" do
      @basic_work_experience.carrer_period = "BCDEFGHIJKLMNOPQRSTU"
      expect(@basic_work_experience).to be_valid
    end

    it "does not allow over 20 character carrer_period" do
      @basic_work_experience.carrer_period = "ABCDEFGHIJKLMNOPQRSTU"
      @basic_work_experience.valid?
      expect(@basic_work_experience.errors[:carrer_period]).to include("は20文字以内で入力してください")
    end

    it "allows 200 character strong_field" do
      @basic_work_experience.strong_field = "BCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
      expect(@basic_work_experience).to be_valid
    end

    it "does not allow over 200 character strong_field" do
      @basic_work_experience.strong_field = "ABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
      @basic_work_experience.valid?
      expect(@basic_work_experience.errors[:strong_field]).to include("は200文字以内で入力してください")
    end

    it "allows 200 character strong_technology" do
      @basic_work_experience.strong_technology = "BCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
      expect(@basic_work_experience).to be_valid
    end

    it "does not allow over 200 character strong_technology" do
      @basic_work_experience.strong_technology = "ABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
      @basic_work_experience.valid?
      expect(@basic_work_experience.errors[:strong_technology]).to include("は200文字以内で入力してください")
    end

    it "allows 200 character strong_business" do
      @basic_work_experience.strong_business = "BCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
      expect(@basic_work_experience).to be_valid
    end

    it "does not allow over 200 character strong_business" do
      @basic_work_experience.strong_business = "ABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
      @basic_work_experience.valid?
      expect(@basic_work_experience.errors[:strong_business]).to include("は200文字以内で入力してください")
    end

    it "allows 200 character self_promotion" do
      @basic_work_experience.self_promotion = ""
      expect(@basic_work_experience).to be_valid
    end

    it "does not allow over 200 character self_promotion" do
      @basic_work_experience.self_promotion = ""
      @basic_work_experience.valid?
      expect(@basic_work_experience.errors[:self_promotion]).to include("は2000文字以内で入力してください")
    end

  end
end
