require 'rails_helper'

RSpec.describe WorkExperience, type: :model do

  # 姓、名、メール、パスワードがあれば有効な状態であること
  it "is valid with a first name, last name, email, and password" do
    introduction = Introduction.new(
      name: 'フジ　太郎',
      furigana: 'フジ　タロウ',
      age: 29,
      address: '港区西麻布３丁目２０−６ 杉友ビル2F',
      content: 'テストデータです！！',
      sex: 0,
      prefecture_id: 1
    )
    work_experience = WorkExperience.new(
      start_month: "2020-12-01",
      end_month: "2021-12-01",
      project_name: "ECサイト構築",
      project_detail: "担当業務",
      role: 15,
      role_description: "SE：2名",
      language: "Java",
      database: "Oracle",
      server: "glassfish",
      fw_mw_tool_etc: "Netbeans",
      responsible_process: "詳細設計",
      introduction: introduction
    )
    expect(work_experience).to be_valid
  end

  it "is invalid over 200 character project_name" do
    work_experience = WorkExperience.new(
      project_name: "ABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
    )
    work_experience.valid?
    expect(work_experience.errors[:project_name]).to include("は200文字以内で入力してください")
  end

  it "is invalid over 2000 character project_detail" do
    work_experience = WorkExperience.new(
      project_detail: ""
    )
    work_experience.valid?
    expect(work_experience.errors[:project_detail]).to include("は2000文字以内で入力してください")
  end

  it "is invalid over 400 character role_description" do
    work_experience = WorkExperience.new(
      role_description: ""
    )
    work_experience.valid?
    expect(work_experience.errors[:role_description]).to include("は400文字以内で入力してください")
  end

  it "is invalid over 400 character language" do
    work_experience = WorkExperience.new(
      language: ""
    )
    work_experience.valid?
    expect(work_experience.errors[:language]).to include("は400文字以内で入力してください")
  end

  it "is invalid over 400 character database" do
    work_experience = WorkExperience.new(
      database: ""
    )
    work_experience.valid?
    expect(work_experience.errors[:database]).to include("は400文字以内で入力してください")
  end

  it "is invalid over 400 character server" do
    work_experience = WorkExperience.new(
      server: ""
    )
    work_experience.valid?
    expect(work_experience.errors[:server]).to include("は400文字以内で入力してください")
  end

  it "is invalid over 400 character fw_mw_tool_etc" do
    work_experience = WorkExperience.new(
      fw_mw_tool_etc: ""
    )
    work_experience.valid?
    expect(work_experience.errors[:fw_mw_tool_etc]).to include("は400文字以内で入力してください")
  end

  it "is invalid over 400 character responsible_process" do
    work_experience = WorkExperience.new(
      responsible_process: ""
    )
    work_experience.valid?
    expect(work_experience.errors[:responsible_process]).to include("は400文字以内で入力してください")
  end
end
