require 'rails_helper'

RSpec.describe WorkExperience, type: :model do
  before do
    @introduction = Introduction.create(
      name: 'フジ　太郎',
      furigana: 'フジ　タロウ',
      age: 29,
      address: '港区西麻布３丁目２０−６ 杉友ビル2F',
      content: 'テストデータです！！',
      sex: 0,
      prefecture_id: 1
    )
    @work_experience = @introduction.work_experiences.create(
      start_month: "2020-12-01",
      end_month: "2021-12-01",
      project_name: "ECサイト構築",
      project_detail: "担当業務",
      role: 15,
      role_description: "SE：2名",
      language: "Java",
      database: "Oracle",
      server: "glassfish",
      fw_mw_tool_etc: "Netbeans",
      responsible_process: "詳細設計",
    )
  end

  it "is valid with a start_month, end_month, project_name, project_detail, role, role_description, language, database, server, fw_mw_tool_etc, and responsible_process" do
    expect(@work_experience).to be_valid
  end

  context "validation" do
    it "allows 200 character project_name" do
      @work_experience.project_name = "BCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
      expect(@work_experience).to be_valid
    end

    it "does not allow over 200 character project_name" do
      @work_experience.project_name = "ABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRSTUVWXYZABCDEFGHIJKLMNOPQRS"
      @work_experience.valid?
      expect(@work_experience.errors[:project_name]).to include("は200文字以内で入力してください")
    end

    it "allows 2000 character project_detail" do
      @work_experience.project_detail = ""
      expect(@work_experience).to be_valid
    end

    it "does not allow over 2000 character project_detail" do
      @work_experience.project_detail = ""
      @work_experience.valid?
      expect(@work_experience.errors[:project_detail]).to include("は2000文字以内で入力してください")
    end

    it "allows 400 character role_description" do
      @work_experience.role_description = ""
      @work_experience.valid?
    end

    it "does not allow over 400 character role_description" do
      @work_experience.role_description = ""
      @work_experience.valid?
      expect(@work_experience.errors[:role_description]).to include("は400文字以内で入力してください")
    end

    it "allows 400 character language" do
      @work_experience.role_description = ""
      @work_experience.valid?
    end

    it "does not allow over 400 character language" do
      @work_experience.language = ""
      @work_experience.valid?
      expect(@work_experience.errors[:language]).to include("は400文字以内で入力してください")
    end

    it "allows 400 character database" do
      @work_experience.role_description = ""
      @work_experience.valid?
    end

    it "does not allow over 400 character database" do
      @work_experience.database = ""
      @work_experience.valid?
      expect(@work_experience.errors[:database]).to include("は400文字以内で入力してください")
    end

    it "allows 400 character server" do
      @work_experience.server = ""
      @work_experience.valid?
    end

    it "does not allow over 400 character server" do
      @work_experience.server = ""
      @work_experience.valid?
      expect(@work_experience.errors[:server]).to include("は400文字以内で入力してください")
    end

    it "allows 400 character fw_mw_tool_etc" do
      @work_experience.fw_mw_tool_etc = ""
      @work_experience.valid?
    end

    it "does not allow over 400 character fw_mw_tool_etc" do
      @work_experience.fw_mw_tool_etc = ""
      @work_experience.valid?
      expect(@work_experience.errors[:fw_mw_tool_etc]).to include("は400文字以内で入力してください")
    end

    it "allows 400 character responsible_process" do
      @work_experience.responsible_process = ""
      @work_experience.valid?
    end

    it "does not allow over 400 character responsible_process" do
      @work_experience.responsible_process = ""
      @work_experience.valid?
      expect(@work_experience.errors[:responsible_process]).to include("は400文字以内で入力してください")
    end
  end

  it "returns bit management value" do
    role_by_bit_management = @work_experience.set_role_by_bit_management([8,2,1])
    expect(role_by_bit_management).to eq 11
  end

  describe "diff start_month and end_month check" do
    context "when diff years are 0" do
      it "returns as xヶ月" do
        @work_experience.start_month = "2022-01-01"
        @work_experience.end_month = "2022-11-30"
        diff_years_months = @work_experience.diff_years_months(@work_experience.start_month, @work_experience.end_month)
        expect(diff_years_months).to eq "11ヶ月"
      end
    end

    context "when diff months are 0" do
      it "returns as x年" do
        @work_experience.start_month = "2022-01-01"
        @work_experience.end_month = "2022-12-31"
        diff_years_months = @work_experience.diff_years_months(@work_experience.start_month, @work_experience.end_month)
        expect(diff_years_months).to eq "1年"
      end
    end

    context "when diff year months are 0" do
      it "returns as x年xヶ月" do
        @work_experience.start_month = "2022-01-01"
        @work_experience.end_month = "2023-11-30"
        diff_years_months = @work_experience.diff_years_months(@work_experience.start_month, @work_experience.end_month)
        expect(diff_years_months).to eq "1年11ヶ月"
      end
    end
  end
end
